frontApp.controller("ImageController",["$scope","$http","$window",function(n,t){n.images=[];n.relatedImages=[];n.image={};n.id=angular.element("#idImage").val();t.get("/API/ImagesAPI").success(function(t){n.images=t});t.get("/API/ImagesAPI/"+n.id).success(function(t){n.image=t});t.get("/API/ImagesAPI?att=imageTuongTu&&value="+n.id).success(function(t){n.relatedImages=t})}]);