frontApp.controller("postController",["$scope","$http","$window","$sce",function(n,t,i,r){n.post={};n.relatedPosts=[];n.idCategory;n.idPost=angular.element("#idPost").val();t.get("/API/PostsAPI/"+n.idPost).success(function(i){n.post=i;n.post.content=r.trustAsHtml(i.content);n.idCategory=i.idCategory;t.get("/API/PostsAPI?att=baiLienQuan&&value="+n.idCategory).success(function(t){angular.forEach(t,function(t){t.idPost!=n.idPost&&n.relatedPosts.push(t)})})})}]);