frontApp.controller("categoryProductController",["$scope","$http","$window","CategoryProduct",function(n,t,i,r){n.categoryProducts=[];n.products=[];n.idCategoryProduct=angular.element("#idCategoryProduct").val();n.product={};t.get("/API/CategoryProductsAPI/").success(function(t){angular.forEach(r.getallCategory(t),function(t){t.idCategoryParent==1&&n.categoryProducts.push(t)})});n.show=function(i){n.id=i;t.get("/API/ProductsAPI/"+i).success(function(t){n.product=t})};t.get("/API/ProductsAPI/").success(function(t){angular.forEach(t,function(t){t.idCategoryProduct==n.idCategoryProduct&&n.products.push(t)})})}]);